import React, { useState } from 'react';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from '../NewExpense/ExpensesFillter';
import './Expenses.css';

const Expenses = (props) => {
  // const { expenses } = props;
  // const helper = expenses.map((expense) => (
  //   <ExpenseItem
  //     title={expense.title}
  //     amount={expense.amount}
  //     date={expense.date}
  //   />
  // ));

  // return <div className='expenses'>{helper}</div>;

  const [filteredYear, setFilteredYear] = useState('2020');

  const filterChangeHandler = (selectedYear) => {
    console.log('in Expenses.js', selectedYear);
    setFilteredYear(selectedYear);
  };

  // const filteredExpenses =props.items.filter(expense=>expense.date.getFullYear()=filteredYear)
  return (
    <Card className='expenses'>
      <ExpensesFilter
        selected={filteredYear}
        onChangeFillter={filterChangeHandler}
      />
      {props.items
        .filter((expense) => {
          expense.date.getFullYear().toString === filteredYear;
        })
        .map((expense) => {
          return (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          );
        })}
    </Card>
  );
};
export default Expenses;
