import ExpenseDate from './ExpenseDate'; //in same folder
import Card from './Card';
import './ExpenseItem.css';

function ExpenseItem(props) {
  //this is just regurler javascript
  //when have props, no need those anymore
  //   const expenseDate = new Date(2021, 2, 28);
  //   const expenseTitle = 'Car Insurance';
  //   const expenseAmount = 294.67;

  return (
    // <div className='expense-item'>
    //replace the <div> to <Card> cus card has the duplicated css style that all those componet used in commen
    <Card className='expense-item'>
      <ExpenseDate date={props.date} />
      <div className='expense-item__description'>
        <h2>{props.title}</h2>
        <div className='expense-item__price'>${props.amount}</div>
      </div>
    </Card>
    // </div>
  );
}

//need to export
export default ExpenseItem;
